property.script:
/** This job inserts references to glossary entries in page contents. */
import org.xwiki.component.util.DefaultParameterizedType;
import org.xwiki.model.reference.DocumentReferenceResolver;
import org.apache.solr.common.SolrDocument;

import org.xwiki.contrib.glossary.GlossaryEntriesTransformer;
import org.xwiki.contrib.glossary.GlossaryModel;
import org.xwiki.contrib.glossary.GlossaryConfiguration;

def logger = services.logging.getLogger('Glossary.Code.GlossaryEntriesTransformer');
try {
  def glossaryConfiguration = services.component.getContextComponentManager().getInstance(GlossaryConfiguration.class, 'default');
  logger.debug('Transformation is activated: ' + glossaryConfiguration.isActivateTransformationJob());
  logger.debug('Target spaces: ' + glossaryConfiguration.getTransformationJobIncludeSpaces());
  logger.debug('Version increment: ' + glossaryConfiguration.isIncrementVersionOnTransformationJob());

  def spaceReferences = glossaryConfiguration.getTransformationJobIncludeSpaces();

  if (glossaryConfiguration.isActivateTransformationJob() && spaceReferences.size() > 0) {
    def spaceQueryClause = "(";
    spaceReferences.eachWithIndex { spaceReference, index ->
      if (index > 0)
        spaceQueryClause += " OR ";
      def spaceName = services.model.serialize(spaceReference);
      spaceQueryClause += "space:(" + spaceName + "*)";
    }
    spaceQueryClause += ")";
    def glossaryModel = services.component.getContextComponentManager().getInstance(GlossaryModel.class, 'default');
    def entries = glossaryModel.getGlossaryEntries();

    def transformer = services.component.getContextComponentManager().getInstance(GlossaryEntriesTransformer.class, 'default');
    def parameterizedType = new DefaultParameterizedType(null, DocumentReferenceResolver.class, SolrDocument.class);
    def solrDocumentResolver = services.component.getContextComponentManager().getInstance(parameterizedType, 'default');

    def wikiDescriptor = services.wiki.currentWikiDescriptor;
    def wikiId = wikiDescriptor.getId();
    entries.each { language, languageEntries ->
      logger.debug("Language: $language")
      languageEntries.each { entryTitle, reference ->
        logger.debug("Entry: [{}] [{}]", entryTitle, reference);
        def solrStatement = "type:DOCUMENT AND \"$entryTitle\" AND $spaceQueryClause AND locale:$language AND wiki:$wikiId";
        logger.debug("Solr statement: [{}]", solrStatement);
        def query = services.query.createQuery(solrStatement, "solr");
        def response = query.execute();
        if (response != null && response.size() > 0) {
          def searchResponse = response.get(0);
          def results = searchResponse.getResults();
          results.each {
            result ->
            logger.debug("Match: [{}] - locale: {}", result.fullname, result.locale)
            def documentReference = solrDocumentResolver.resolve(result);
            def page = xwiki.getDocument(documentReference);
            def translatedPage = page.getTranslatedDocument(result.locale).getDocument().clone();
            def content = translatedPage.getContent();
            def syntax = translatedPage.getSyntax();
            def xdom = services.rendering.parse(content, syntax.toIdString());
            def locale = services.localization.toLocale(result.locale)
            def transformed = transformer.transformGlossaryEntries(xdom, syntax, locale);
            if (transformed) {
              def transformedContent = services.rendering.render(xdom, syntax.toIdString());
              translatedPage.setContent(transformedContent);
              if (!glossaryConfiguration.isIncrementVersionOnTransformationJob()) {
                translatedPage.setContentDirty(false);
                translatedPage.setMetaDataDirty(false);
              }
              xwiki.getXWiki().saveDocument(translatedPage, services.localization.render('glossary.transformationJob.save.message'), true, xcontext);
            }
          }
        }
      }
    }
  }
} catch (Exception e) {
  logger.error('An error occurred while running the glossary transformation job: [{}]', e.getMessage(), e);
}
